import styled from '@emotion/styled';

export default styled.div`
    .form-error-message {
        font-size: 1.4em;
        color: red;
        font-weight: 300;
        margin-bottom: 1em;
        padding-bottom: 1em;
        text-align: center;
        border-bottom: .1em solid white;
    }
`;
