import { HYDRATE } from 'next-redux-wrapper';

import * as types from './types';
import Element from 'types/redux/Element';

import { INIT_FLAG, SUCCESS_FLAG, FAIL_FLAG, ELEMENT_MARKUP, LIST_MARKUP } from 'consts/redux';

import { API_RESOURCE_PRODUCT } from 'consts/apiResources';

import Product from 'models/Product';

export interface State {
    mapById: {
        [key: string]: Element<Product>;
    };
}

export const initialState = {
    mapById: {},
};

export default function ProductReducer(state: State = initialState, action: any): any {
    switch (action.type) {
        case HYDRATE:
            // Attention! This will overwrite client state! Real apps should use proper reconciliation.
            return { ...state, ...action.payload };

            case types.SINGLE + '_' + INIT_FLAG:
                return {
                    ...state,
                    mapById: {
                        ...state.mapById,
                        [action.params.id]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapById[action.params.id] || {}),
                            isLoading: true,
                            isLoaded: false,
                        },
                    },
                };
            case types.SINGLE + '_' + SUCCESS_FLAG:
                return {
                    ...state,
                    mapById: {
                        ...state.mapById,
                        [action.params.id]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapById[action.params.id] || {}),
                            isLoading: false,
                            isLoaded: true,
                            data: new Product(action.payload.data[API_RESOURCE_PRODUCT]),
                            error: false,
                        },
                    },
                };
            case types.SINGLE + '_' + FAIL_FLAG:
                return {
                    ...state,
                    mapById: {
                        ...state.mapById,
                        [action.params.id]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapById[action.params.id] || {}),
                            isLoading: false,
                            isLoaded: true,
                            data: null,
                            error: true,
                        },
                    },
                };

        default:
            return state;
    }
}
