import { HYDRATE } from 'next-redux-wrapper';

import * as types from './types';
import { ActionResponse } from 'types/redux/ActionResponse';
import ListCollection from 'types/redux/ListCollection';
import Element from 'types/redux/Element';
import { INIT_FLAG, SUCCESS_FLAG, FAIL_FLAG, LIST_MARKUP, ELEMENT_MARKUP } from 'consts/redux';
import { 
    API_RESOURCE_ARTICLES,
    API_RESOURCE_ARTICLE,
} from 'consts/apiResources';

import ApiArticle from 'types/api/Article';
import Article from 'models/Article';

export interface State {
    list: ListCollection<Article>;
    mapById: {
        [key: string]: Element<Article>;
    };
}

export const initialState = {
    list: LIST_MARKUP,
    mapById: {},
};

export default function PublicArticlesReducer(state: State = initialState, action: ActionResponse): any {
    switch (action.type) {
        case HYDRATE:
            // Attention! This will overwrite client state! Real apps should use proper reconciliation.
            return { ...state, ...action.payload };

        case types.LIST + '_' + INIT_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    params: action.params,
                    isLoading: true,
                    isLoaded: false,
                },
            };
        case types.LIST + '_' + SUCCESS_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    isLoading: false,
                    isLoaded: true,
                    elements: action.payload.data[API_RESOURCE_ARTICLES]
                        .elements
                        .map((elem: ApiArticle) => new Article(elem)),
                    meta: action.payload.data[API_RESOURCE_ARTICLES].meta,
                    error: false,
                },
            };
        case types.LIST + '_' + FAIL_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    isLoading: false,
                    isLoaded: true,
                    error: true,
                },
            };

        case types.SINGLE + '_' + INIT_FLAG:
            return {
                ...state,
                mapById: {
                    ...state.mapById,
                    [action.params.id]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapById[action.params.id] || {}),
                        isLoading: true,
                        isLoaded: false,
                    },
                },
            };
        case types.SINGLE + '_' + SUCCESS_FLAG:
            return {
                ...state,
                mapById: {
                    ...state.mapById,
                    [action.params.id]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapById[action.params.id] || {}),
                        isLoading: false,
                        isLoaded: true,
                        data: new Article(action.payload.data[API_RESOURCE_ARTICLE]),
                        error: false,
                    },
                },
            };
        case types.SINGLE + '_' + FAIL_FLAG:
            return {
                ...state,
                mapById: {
                    ...state.mapById,
                    [action.params.id]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapById[action.params.id] || {}),
                        isLoading: false,
                        isLoaded: true,
                        data: null,
                        error: true,
                    },
                },
            };

        default:
            return state;
    }
}
