import styled from '@emotion/styled';
import variables from 'theme/styles/vars';

export default styled.div`
    position: relative;
    display: flex;
    align-items: stretch;
    justify-content: center;

    .layout-container {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        align-items: center;
        width: 100%;
        position: absolute;
        bottom: 0;
        margin-bottom: 3.5em
    }
`;
