import styled from '@emotion/styled';
import variables from 'theme/styles/vars';

export default styled.div`
    .layout-container {
        .headline {
            text-align: center;
            font-size: 3em;
            font-weight: 600;
        }

        .login-form-wrapper {
            display: flex;
            justify-content: center;
            margin-top: 2em;

            .layout-forms-form {
                width: 30%;
                form {
                    .layout-forms-input-row {
                        .layout-forms-input {
                            .layout-forms-input-wrapper {
                                margin-top: 0;
                                margin-bottom: .1em;
                            }
                        }
                    }
                }
            }
        }
    }
`;
