import styled from '@emotion/styled';
import variables from 'theme/styles/vars';

export default styled.div`


    @media all and (max-width: ${variables.tabletL}) {
        .top {
            display: none;
        }

        .main {
            .layout-container {
                .col {
                    &.col-left {
                        .layout-nav-section { 
                            display: none;
                        }
                    }
                    &.col-right {
                        .layout-nav-section { 
                            display: none;
                        }

                        .login-button {
                            display: none;
                        }

                        .mobile-nav-trigger {
                            display: block;
                        }
                    }
                }
            }
        }

        &.expanded {
            .mobile-nav {
                opacity: 1;
                transform: translateX(0%);
            }
        }
    }
`;
