import { HYDRATE } from 'next-redux-wrapper';

import * as types from './types';
import { ActionResponse } from 'types/redux/ActionResponse';
import ListCollection from 'types/redux/ListCollection';
import Element from 'types/redux/Element';
import { INIT_FLAG, SUCCESS_FLAG, FAIL_FLAG, LIST_MARKUP, ELEMENT_MARKUP } from 'consts/redux';
import { 
    API_RESOURCE_TRAINERS,
    API_RESOURCE_USER
} from 'consts/apiResources';

import ApiUser from 'types/api/User';
import User from 'models/User';

export interface State {
    list: ListCollection<User>;
    mapBySlug: {
        [key: string]: Element<User>;
    };
}

export const initialState = {
    list: LIST_MARKUP,
    mapBySlug: {},
};

export default function PublicUsersReducer(state: State = initialState, action: ActionResponse): any {
    switch (action.type) {
        case HYDRATE:
            // Attention! This will overwrite client state! Real apps should use proper reconciliation.
            return { ...state, ...action.payload };

        case types.LIST_TRAINERS + '_' + INIT_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    params: action.params,
                    isLoading: true,
                    isLoaded: false,
                },
            };
        case types.LIST_TRAINERS + '_' + SUCCESS_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    isLoading: false,
                    isLoaded: true,
                    elements: action.payload.data[API_RESOURCE_TRAINERS]
                        .elements
                        .map((elem: ApiUser) => new User(elem)),
                    meta: action.payload.data[API_RESOURCE_TRAINERS].meta,
                    error: false,
                },
            };
        case types.LIST_TRAINERS + '_' + FAIL_FLAG:
            return {
                ...state,
                list: {
                    ...LIST_MARKUP,
                    ...state.list,
                    isLoading: false,
                    isLoaded: true,
                    error: true,
                },
            };

        case types.SINGLE_BY_SLUG + '_' + INIT_FLAG:
            return {
                ...state,
                mapBySlug: {
                    ...state.mapBySlug,
                    [action.params.slug]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapBySlug[action.params.slug] || {}),
                        isLoading: true,
                        isLoaded: false,
                    },
                },
            };
        case types.SINGLE_BY_SLUG + '_' + SUCCESS_FLAG:
            return {
                ...state,
                mapBySlug: {
                    ...state.mapBySlug,
                    [action.params.slug]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapBySlug[action.params.slug] || {}),
                        isLoading: false,
                        isLoaded: true,
                        data: new User(action.payload.data[API_RESOURCE_USER]),
                        error: false,
                    },
                },
            };
        case types.SINGLE_BY_SLUG + '_' + FAIL_FLAG:
            return {
                ...state,
                mapBySlug: {
                    ...state.mapBySlug,
                    [action.params.slug]: {
                        ...ELEMENT_MARKUP,
                        ...(state.mapBySlug[action.params.slug] || {}),
                        isLoading: false,
                        isLoaded: true,
                        data: null,
                        error: true,
                    },
                },
            };
        default:
            return state;
    }
}
