import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { wrapper } from 'store';
import { getLocale } from 'utils/locale';
import Component from './component';
import { list as listEffects } from 'store/modules/userEffects/actions';
Component.getInitialProps = wrapper.getInitialPageProps(store => async ({
  req,
  res,
  query
}) => {
  const locale = getLocale();
  await store.dispatch(listEffects({
    perPage: 999,
    promoted: true
  }));
  return {
    data: locale.home
  };
});

const mapStateToProps = state => {
  return {
    state: {
      userEffects: state.userEffects.list
    }
  };
};

const mapDispatchToProps = dispatch => {
  return {
    actions: {
      listEffects: bindActionCreators(listEffects, dispatch)
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Component);
