import { FunctionComponent, useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDown } from '@fortawesome/free-solid-svg-icons';
import classnames from 'classnames';

import { Props } from './index';

import StyledComponent from './styles';
import LayoutContainer, { Sizes } from 'components/layout/LayoutContainer';
import Button from 'components/layout/Button';

const PublicPageHomeSectionHero: FunctionComponent<Props> = ({ headline, buttons }) => {
    const [element, setElement] = useState(null);

    useEffect(() => {
        setElement(document.querySelector("#public-page-home-section-offers"));
    });

    let scrollTo = undefined;
    if (element) {
        scrollTo = element.offsetTop;
    }
    console.log('123');

    return (
        <>
            <div className="video">
                <video muted loop autoPlay playsInline>
                    <source src="/videos/hero_background.mp4" type="video/mp4"/>       
                </video>
            </div>
            <StyledComponent
                className={classnames(
                    'public-page-home-section-hero',
                )}
            >
                <LayoutContainer size={Sizes.Large}>
                    <div className="copy">
                        <h1 
                            className="headline"
                            dangerouslySetInnerHTML={{ __html: headline }}
                        />
                    </div>
                    {Array.isArray(buttons) && buttons.length > 0 && (
                        <div className="buttons">
                            {buttons.map(button => (
                                <Button
                                    key={button.key} 
                                    {...button} 
                                />
                            ))}
                        </div>
                    )}
                    <div className="arrow-down">
                        <a className="arrow-down-button" onClick={() => window.scrollTo(0, scrollTo)}>
                            <FontAwesomeIcon icon={faArrowDown}/>
                        </a>
                    </div>
                </LayoutContainer>
            </StyledComponent>
        </>
    )
};

export default PublicPageHomeSectionHero;
