import { HYDRATE } from 'next-redux-wrapper';

import * as types from './types';
import Element from 'types/redux/Element';

import { INIT_FLAG, SUCCESS_FLAG, FAIL_FLAG, ELEMENT_MARKUP, LIST_MARKUP } from 'consts/redux';

import { API_RESOURCE_SHOP_PAGE } from 'consts/apiResources';

import ShopPage from 'models/ShopPage';

export interface State {
    mapBySlug: {
        [key: string]: Element<ShopPage>;
    };
}

export const initialState = {
    mapBySlug: {},
};

export default function ShopPageReducer(state: State = initialState, action: any): any {
    switch (action.type) {
        case HYDRATE:
            // Attention! This will overwrite client state! Real apps should use proper reconciliation.
            return { ...state, ...action.payload };

            case types.SINGLE_BY_SLUG + '_' + INIT_FLAG:
                return {
                    ...state,
                    mapBySlug: {
                        ...state.mapBySlug,
                        [action.params.slug]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapBySlug[action.params.slug] || {}),
                            isLoading: true,
                            isLoaded: false,
                        },
                    },
                };
            case types.SINGLE_BY_SLUG + '_' + SUCCESS_FLAG:
                return {
                    ...state,
                    mapBySlug: {
                        ...state.mapBySlug,
                        [action.params.slug]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapBySlug[action.params.slug] || {}),
                            isLoading: false,
                            isLoaded: true,
                            data: new ShopPage(action.payload.data[API_RESOURCE_SHOP_PAGE]),
                            error: false,
                        },
                    },
                };
            case types.SINGLE_BY_SLUG + '_' + FAIL_FLAG:
                return {
                    ...state,
                    mapBySlug: {
                        ...state.mapBySlug,
                        [action.params.slug]: {
                            ...ELEMENT_MARKUP,
                            ...(state.mapBySlug[action.params.slug] || {}),
                            isLoading: false,
                            isLoaded: true,
                            data: null,
                            error: true,
                        },
                    },
                };

        default:
            return state;
    }
}
